func orientation(image types.ImageData) string {
	h, err := strconv.Atoi(image.Height)
	if err != nil {
		fmt.Println(err)
		h = 0
	}

	w, err := strconv.Atoi(image.Width)
	if err != nil {
		fmt.Println(err)
		w = 0
	}

	if w > h {
		return "flex-col"
	}

	return "flex"
}
